/**
 * A DOMString containing the context identifier defining the drawing context associated to the canvas.
 */
export type ContextType = "2d" | "webgl" | "experimental-webgl" | "webgl2" | "webgl2-compute" | "bitmaprenderer" | "gpupresent" | "webgpu";
/**
 * Options for canvas creation. All optional.
 */
export type CanvasContextOptions = {
    /**
     * Request an initial canvas width.
     */
    width?: number;
    /**
     * Request an initial canvas height.
     */
    height?: number;
    /**
     * Request an offscreen canvas.
     */
    offscreen?: boolean;
    /**
     * Handle use in a worker.
     */
    worker?: boolean;
    /**
     * Attributes to be passed to getContext.
     */
    contextAttributes?: CanvasRenderingContext2DSettings | WebGLContextAttributes;
    /**
     * Canvas element to use.
     */
    canvas?: HTMLCanvasElement;
};
export type CanvasContextReturnValue = {
    canvas: HTMLCanvasElement | OffscreenCanvas;
    context: RenderingContext;
};
